// src/components/JoinUs.js
import React from 'react';
import { Typography, Box } from '@mui/material';
import uiucLogo from "../"
function JoinUs() {
  return (
    <Box 
      sx={{
        width: '100%',
        minHeight: '80vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#2b2b2b',
        color: 'white',
        textAlign: 'center',
        overflowX: 'hidden', 
      }}
    >
      <Typography variant="h4" gutterBottom sx={{ maxWidth: '90%', margin: '0 auto', textShadow: '2px 2px 5px rgba(0, 0, 0, 0.5)', borderRadius: '10px' }}>
        <strong>Join Us</strong><br/><br/>
      </Typography>
      <Typography variant="h6" gutterBottom sx={{ maxWidth: '90%', margin: '0 auto', textShadow: '2px 2px 5px rgba(0, 0, 0, 0.5)', borderRadius: '10px' }}>
        We are actively looking for strong and self-motivated PhD students to join DASSL at UIUC. Please apply to the UIUC CS PhD program and mention Aishwarya Ganesan (aganesn2@illinois.edu) or Ram Alagappan (ramn@illinois.edu) as potential advisors in your application. UIUC has a stellar systems group (see csrankings) that you can be a part of and contribute.<br/><br/>
        At DASSL, you'll gain hands-on experience with advanced system design, contribute to influential research publications, and prepare for a successful career in academia or industry. Explore how high-performance computing and innovative data management can address some of the most challenging problems facing technology today.
      </Typography>
      <Box sx={{ mt: 4 }}>
        <img src="path_to_image" alt="Group" style={{ borderRadius: '50%', width: '100%', maxWidth: '200px' }} />
      </Box>
    </Box>
  );
}

export default JoinUs;
