// src/App.js
import React from 'react';
import { ThemeProvider, Box, Container, Link as MuiLink } from '@mui/material';
import theme from './theme';
import Navbar from './components/Navbar';
import News from './components/News';
import People from './components/People';
import Projects from './components/Projects';
import Publications from './components/Publications';
import Software from './components/Software';
import JoinUs from './components/JoinUs';
import Home from './components/Home';
import ScrollToTop from './components/ScrollToTop';
import backgroundImage from './assets/background.png'; // Ensure the image is correctly imported

function App() {
  return (
    <ThemeProvider theme={theme}>
      <ScrollToTop />
      <Box
        sx={{
          display: 'flex',
          flexDirection: 'column',
          minHeight: '100vh',
          background: `url(${backgroundImage}) no-repeat center center fixed`,
          backgroundSize: 'cover',
          paddingTop: '64px', // Adjust based on the Navbar height
          paddingBottom: 4,
        }}
      >
        <Navbar />
        <Container sx={{ flex: '1 0 auto', overflowY: 'auto', paddingTop: '64px' }}>
          <Box id="home">
            <Home />
          </Box>
          <Box id="news" sx={{ mt: 8 }}>
            <News />
          </Box>
          <Box id="people" sx={{ mt: 8 }}>
            <People />
          </Box>
          <Box id="projects" sx={{ mt: 8 }}>
            <Projects />
          </Box>
          <Box id="publications" sx={{ mt: 8 }}>
            <Publications />
          </Box>
          <Box id="software" sx={{ mt: 8 }}>
            <Software />
          </Box>
          <Box id="join" sx={{ mt: 8 }}>
            <JoinUs />
          </Box>
        </Container>
      </Box>
    </ThemeProvider>
  );
}

export default App;
