// src/components/Home.js
import React from 'react';
import { Typography, Box, Container } from '@mui/material';
import uiucLogo from '../assets/dassl.png'; 
import backgroundImage from '../assets/layer5.jpg'; 

function Home() {
  return (
    <Box 
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        minHeight: '100vh',
        background: `linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        color: 'white',
        textAlign: 'center',
        padding: '80px 20px',
      }}
    >
      <Box sx={{ mb: 4 }}>
        <img src={uiucLogo} alt="UIUC Logo" style={{ maxWidth: '200px', height: 'auto', marginBottom: '20px' }} />
      </Box>
      <Typography variant="h3" component="h1" gutterBottom sx={{ fontWeight: 'bold', textShadow: '2px 2px 4px rgba(0, 0, 0, 0.7)' }}>
        Distributed and Storage Systems Lab
      </Typography>
      <Typography variant="subtitle1" gutterBottom sx={{ maxWidth: '800px', margin: '0 auto', textShadow: '1px 1px 3px rgba(0, 0, 0, 0.5)' }}>
        The Distributed and Storage Systems Lab (DASSL) at the University of Illinois at Urbana-Champaign focuses on 
        designing and developing robust, high-performance software systems that form the core of our modern computing 
        infrastructure. Our research spans distributed systems, storage systems, and real-world application implications, 
        making systems more reliable, scalable, and efficient. Our collaborative environment, coupled with cutting-edge 
        resources, drives innovation and advancement in computational technology.
      </Typography>
    </Box>
  );
}

export default Home;
