// src/components/AboutUs.js
import React from 'react';
import { Typography, Box } from '@mui/material';
import backgroundImage from '../assets/bg.png'; 

function AboutUs() {
  return (
    <Box 
      sx={{
        width: '100vw',
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        background: `url(${backgroundImage}) no-repeat center center fixed`,
        backgroundSize: 'cover',
        color: 'white',
        textAlign: 'center',
        padding: '40px',
      }}
    >
      <Typography variant="h2" component="h1" gutterBottom sx={{ fontWeight: 'bold', textShadow: '3px 3px 6px rgba(0, 0, 0, 0.7)' }}>
        About Us
      </Typography>
      <Typography variant="h5" gutterBottom sx={{ maxWidth: '80%', margin: '0 auto', textShadow: '2px 2px 5px rgba(0, 0, 0, 0.5)', backgroundColor: 'rgba(0, 0, 0, 0.9)', padding: '30px', borderRadius: '10px' }}>
        The Distributed and Storage Systems Lab (DASSL) at the University of Illinois at Urbana-Champaign focuses on 
        designing and developing robust, high-performance software systems that form the core of our modern computing 
        infrastructure. Our research spans distributed systems, storage systems, and real-world application implications, 
        making systems more reliable, scalable, and efficient. Our collaborative environment, coupled with cutting-edge 
        resources, drives innovation and advancement in computational technology.
      </Typography>
      <Typography variant="h5" gutterBottom sx={{ maxWidth: '80%', margin: '20px auto', textShadow: '2px 2px 5px rgba(0, 0, 0, 0.5)', backgroundColor: 'rgba(0, 0, 0, 0.9)', padding: '30px', borderRadius: '10px' }}>
        We are actively seeking motivated PhD students, postdocs, and visiting scholars who are passionate about research in distributed systems and storage technologies. By joining DASSL, you'll work alongside leaders in the field to create solutions that impact the real world. Please apply to the UIUC CS PhD program and mention your interest in working with Professors Aishwarya Ganesan or Ram Alagappan.
      </Typography>
      <Typography variant="h5" gutterBottom sx={{ maxWidth: '80%', margin: '20px auto', textShadow: '2px 2px 5px rgba(0, 0, 0, 0.5)', backgroundColor: 'rgba(0, 0, 0, 0.9)', padding: '30px', borderRadius: '10px' }}>
        At DASSL, you'll gain hands-on experience with advanced system design, contribute to influential research publications, and prepare for a successful career in academia or industry. Explore how high-performance computing and innovative data management can address some of the most challenging problems facing technology today.
      </Typography>
    </Box>
  );
}

export default AboutUs;
