import React, { useState, useEffect } from 'react';
import { Typography, Box, Link } from '@mui/material';

function News() {
  const newsItems = [
    { text: "Ram Alagappan wins the NSF CAREER Award!" },
    { text: "Aishwarya Ganesan wins the NSF CAREER Award!" },
    { text: "Our work on high-performance KV-store replication will appear in Fast '24. Congrats Henry!" },
    { text: "Congrats Xuhao on clearing his PhD qualification exam!" },
    { text: "Our work on disaggregated storage fault-tolerance will appear in EuroSys '24. Congrats Xuhao!" },
    { 
      text: "Aishwarya and Ram were interviewed by TigerBeetle's CEO Joran, and Chaitanya (who interned there) - ",
      link: "https://www.youtube.com/watch?v=1rArr0yb3zg&t=2s",
      linkText: "Watch the video."
    }
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % newsItems.length);
    }, 5000); // Change text every 5 seconds

    return () => clearInterval(interval);
  }, [newsItems.length]);

  return (
    <Box sx={{
      mt: 4,
      padding: 3,
      backgroundColor: 'black',
      color: 'white',
      overflow: 'hidden',
      whiteSpace: 'nowrap',
    }}>
      <Typography variant="h4" sx={{ mb: 2, fontWeight: 'bold', textShadow: '2px 2px 4px rgba(0, 0, 0, 0.5)' }}>
        News:
      </Typography>
      <Box sx={{
        display: 'flex',
        alignItems: 'center',
        height: '50px', // Adjust as needed
        overflow: 'hidden',
        position: 'relative',
      }}>
        <Box sx={{
          display: 'inline-block',
          position: 'absolute',
          whiteSpace: 'nowrap',
          animation: `slide 5s linear infinite`,
          '@keyframes slide': {
            '0%': { transform: 'translateX(100%)' },
            '100%': { transform: 'translateX(-100%)' }
          }
        }}>
          <Typography variant="body1" sx={{ fontSize: '1.1rem', color: 'white' }}>
            {newsItems[currentIndex].text}
            {newsItems[currentIndex].link && (
              <Link href={newsItems[currentIndex].link} sx={{ color: 'lightblue', textDecoration: 'underline', ml: 1 }} target="_blank" rel="noopener">
                {newsItems[currentIndex].linkText}
              </Link>
            )}
          </Typography>
        </Box>
      </Box>
    </Box>
  );
}

export default News;
