import React from 'react';
import { Typography, Card, CardContent, Container } from '@mui/material';
import backgroundImage from '../assets/layer5.jpg'; // Path to your background image

function JoinUs() {
  return (
    <Container sx={{
      mt: 4,
      backgroundImage: `url(${backgroundImage})`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundRepeat: 'no-repeat',
      borderRadius: '16px',
      padding: 3,
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.5)',
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      color: 'white',
    }}>
      <Typography variant="h4" sx={{ mb: 2, fontWeight: 'bold', textShadow: '2px 2px 4px rgba(0, 0, 0, 0.5)' }}>
        Join Us!
      </Typography>
      <Card sx={{
        backgroundColor: 'rgba(255, 255, 255, 0.5)',
        borderRadius: '8px',
        boxShadow: '0 2px 4px rgba(0, 0, 0, 0.5)'
      }}>
        <CardContent>
          <Typography variant="body1" paragraph sx={{ color: 'black' }}>
            We are actively seeking motivated PhD students, postdocs, and visiting scholars who are passionate about research in distributed systems and storage technologies. By joining DASSL, you'll work alongside leaders in the field to create solutions that impact the real world. Please apply to the UIUC CS PhD program and mention your interest in working with Professors Aishwarya Ganesan or Ram Alagappan.
          </Typography>
          <Typography variant="body1" paragraph sx={{ color: 'black' }}>
            At DASSL, you'll gain hands-on experience with advanced system design, contribute to influential research publications, and prepare for a successful career in academia or industry. Explore how high-performance computing and innovative data management can address some of the most challenging problems facing technology today.
          </Typography>
        </CardContent>
      </Card>
    </Container>
  );
}

export default JoinUs;
