import React from 'react';
import { Typography, Card, CardContent, Grid, Container } from '@mui/material';
import backgroundImage from '../assets/layer5.jpg'; 

function Projects() {
  const projects = [
    "Rethinking distributed protocols with storage in mind",
    "Storage systems for disaggregated data centers",
    "Applying ML advances to systems",
    "Storage systems for emerging hardware"
  ];

  return (
    <Container sx={{
      mt: 3, // Minimized top margin
      mb: 0, // Minimized bottom margin
      backgroundImage: `url(${backgroundImage})`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundRepeat: 'no-repeat',
      borderRadius: '16px',
      padding: 3, // Reduced overall padding
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.5)',
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      overflow: 'hidden', 
      maxHeight: '90vh', 
      boxSizing: 'border-box'
    }}>
      <Typography variant="h4" sx={{
        color: 'white', 
        fontWeight: 'bold', 
        textShadow: '2px 2px 4px rgba(0, 0, 0, 0.5)',
        mb: 2
      }}>
        Projects
      </Typography>
      <Grid container spacing={2} sx={{
        overflow: 'auto', 
        maxHeight: 'calc(90vh - 100px)' // Ensures grid does not exceed the container's height minus header
      }}>
        {projects.map((project, index) => (
          <Grid item xs={12} key={index}>
            <Card sx={{
              backgroundColor: 'rgba(255, 255, 255, 0.5)',
              borderRadius: '8px',
              boxShadow: '0 2px 4px rgba(0, 0, 0, 0.5)'
            }}>
              <CardContent>
                <Typography variant="h6" sx={{ color: 'black' }}>
                  {project}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}

export default Projects;
