// src/components/News.js
import React from 'react';
import { Typography, Card, CardContent, Grid, Container, Link } from '@mui/material';
import backgroundImage from '../assets/layer5.jpg'; // Ensure the path to your background image is correct

function News() {
  const newsItems = [
    { text: "Ram Alagappan wins the NSF CAREER Award!" },
    { text: "Aishwarya Ganesan wins the NSF CAREER Award!" },
    { text: "Our work on high-performance KV-store replication will appear in Fast '24. Congrats Henry!" },
    { text: "Congrats Xuhao on clearing his PhD qualification exam!" },
    { text: "Our work on disaggregated storage fault-tolerance will appear in EuroSys '24. Congrats Xuhao!" },
    { 
      text: "Aishwarya and Ram were interviewed by TigerBeetle's CEO Joran, and Chaitanya (who interned there) - ",
      link: "https://www.youtube.com/watch?v=1rArr0yb3zg&t=2s",
      linkText: "Watch the video."
    }
  ];

  return (
    <Container sx={{
      mt: 4,
      backgroundImage: `url(${backgroundImage})`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundRepeat: 'no-repeat',
      borderRadius: '16px',
      padding: 3,
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.5)',
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      color: 'white', 
      overflowY: 'auto',
      maxHeight: '80vh' // Adjust as needed
    }}>
      <Typography variant="h4" sx={{ mb: 4, fontWeight: 'bold', textShadow: '2px 2px 4px rgba(0, 0, 0, 0.5)' }}>
        News
      </Typography>
      <Grid container spacing={2}>
        {newsItems.map((item, index) => (
          <Grid item xs={12} key={index}>
            <Card sx={{
              backgroundColor: 'rgba(255, 255, 255, 0.5)',
              borderRadius: '8px',
              boxShadow: '0 2px 4px rgba(0, 0, 0, 0.5)'
            }}>
              <CardContent>
                <Typography variant="body1" sx={{ fontSize: '1.1rem', color: 'black' }}>
                  {item.text}
                  {item.link && <Link href={item.link} sx={{ color: 'darkblue', textDecoration: 'underline' }} target="_blank" rel="noopener">{item.linkText}</Link>}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}

export default News;
